// indexedDB.js
// #offline

const DB_NAME = 'ACookbookDB';
const STORE_NAME = 'recipes';
const DB_VERSION = 1;

export function openDB() {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open(DB_NAME, DB_VERSION);

    request.onupgradeneeded = (e) => {
      const db = e.target.result;
      if (!db.objectStoreNames.contains(STORE_NAME)) {
        db.createObjectStore(STORE_NAME, { keyPath: '_id' });
        console.log('[IndexedDB] Object store created ✅');
      }
    };

    request.onsuccess = () => resolve(request.result);
    request.onerror = () => reject(request.error);
  });
}

// Save all recipes from API
export async function saveRecipes(recipes) {
  const db = await openDB();
  const tx = db.transaction(STORE_NAME, 'readwrite');
  const store = tx.objectStore(STORE_NAME);
  recipes.forEach(recipe => {
    store.put(recipe);
    console.log('[IndexedDB] Saved recipe:', recipe.title); // debug
  });
  await tx.done;
  db.close();
}

// Get all recipes for offline use
export async function getStoredRecipes() {
  const db = await openDB();
  const tx = db.transaction(STORE_NAME, 'readonly');
  const store = tx.objectStore(STORE_NAME);
  const all = await store.getAll();
  db.close();
  console.log('[IndexedDB] Retrieved', all.length, 'recipes');
  return all;
}
